import React from 'react'
import {Route, Switch} from "react-router";

import Users from 'pages/users'
import Home from "pages/home";
import Login from "pages/login";
import Profile from "pages/profile";
import ProfileEdit from "pages/profileEdit";
import Chat from "pages/chat";
import Dialogs from "pages/dialogs";
import Dialog from "pages/dialog";
import TodoList from "pages/todoList";
import WeatherMore from "components/weather/weatherMore";
import Currencies from "components/currency/currencies";
import GoogleMapContainer from "components/map/googleMap/myGoogleMap";
import MyYandexMap from "components/map/yandexMap/myYandexMap";

const Content = () => {

    return(
            <Switch>
                <Route exact path="/" component={Home}/>
                <Route path="/users" component={Users}/>
                <Route path="/login" component={Login}/>
                <Route path="/profile/:userId" component={Profile}/>
                <Route path="/edit/:userId" component={ProfileEdit}/>
                <Route path="/chat" component={Chat}/>
                <Route exact path="/dialogs" component={Dialogs}/>
                <Route exact path="/dialogs/:userId/messages" component={Dialog}/>
                <Route exact path="/todo" component={TodoList}/>
                <Route exact path="/weather/:city" component={WeatherMore}/>
                <Route exact path="/currencies" component={Currencies}/>
                {/*<Route exact path="/map" component={GoogleMapContainer} />*/}
                <Route exact path="/map" component={MyYandexMap} />
                <Route path="*" render={() => <div>404 NOT FOUND</div>}/>
            </Switch>
    )
}

export default Content
