import React, {useEffect, useState} from 'react'
import {fetchCurrencies} from "api";

const Currencies = () => {
    const [currencies, setCurrencies] = useState({})
    const keys = Object.keys(currencies)
    // console.log('currencies', currencies)
    // console.log('keys', Object.keys(currencies))

    useEffect(async () => {
        try {
            const curr = await fetchCurrencies().then(res => res.Valute)
            setCurrencies(curr)
        }catch (err){
            console.log('Error ', err)
        }
    }, [])

    return (
        <div>
            {currencies &&
            <table className="table table-striped table-hover">
                <thead>
                <tr>
                    <th scope="col">Name</th>
                    <th scope="col">CharCode</th>
                    <th scope="col">Nominal</th>
                    <th scope="col">Value</th>
                </tr>
                </thead>
                <tbody>
                {keys.map((key) => (
                    <tr key={key}>
                        <td><b>{currencies[key].Name}</b></td>
                        <td>{currencies[key].CharCode}</td>
                        <td>{currencies[key].Nominal}</td>
                        <td>{currencies[key].Value}</td>
                    </tr>
                ))}
                </tbody>
            </table>
            }
        </div>
    )
}

export default Currencies