import React from 'react'

const DateComponent = () => {
    const days = [
        'Воскресенье',
        'Понедельник',
        'Вторник',
        'Среда',
        'Четверг',
        'Пятница',
        'Суббота'
    ];
    const d = new Date()
    const date = d.toLocaleDateString()
    const n = d.getDay()
    return(
        <div><h5>{date}, {days[n]}</h5></div>
    )
}

export default DateComponent