import React, {useEffect, useState} from 'react'

const Paginator = ({totalUsersCount, onPageChanged, currentPage, pageSize}) => {
    // console.log('totalItemsCount', totalItemsCount)
    const [pageNumber, setPageNumber] = useState(1)
    const [leftPortionPageNumber, setLeftPortionPageNumber] = useState(0)
    const [rightPortionPageNumber, setRightPortionPageNumber] = useState(0)

    // console.log('pageNumber', pageNumber)

    const pagesCount = Math.ceil(totalUsersCount / pageSize)
    const pages = []
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i)
    }
    // console.log('Array pages', pages)

    useEffect(() => {
        setLeftPortionPageNumber((pageNumber - 1) * pageSize + 1)
        setRightPortionPageNumber(pageNumber * pageSize)
    })


    return (
        <div className="pagination">
            {pageNumber > 1 &&
            <button
                className="btn btn-light"
                onClick={() => {
                    setPageNumber(pageNumber - 1)
                }}
            >
                PREV
            </button>
            }
            {pages.filter(p => p >= leftPortionPageNumber && p <= rightPortionPageNumber)
                .map((p) => (
                    <button style={{"margin": "2px"}}
                            className={currentPage === p ? "btn btn-primary" : "btn btn-light"}
                            key={p}
                            onClick={(e) => {
                                onPageChanged(p);
                            }}
                    >
                        {p}
                    </button>
                ))}
            {pagesCount > pageNumber &&
            <button
                className="btn btn-light"
                onClick={() => {
                    setPageNumber(pageNumber + 1)
                }}
            >
                NEXT
            </button>}
        </div>
    )
}

export default Paginator