import React, {useEffect, useState} from 'react'

const Watch = () => {
    const [time, setTime] = useState('')

    useEffect(() => {
        const clockInterval = setInterval(() => {
            const d = new Date().toLocaleTimeString()
            setTime(d)
        }, 1000)
        return () => clearInterval(clockInterval)
    }, [])

    return (
        <div><h5>{time}</h5></div>
    )
}

export default Watch